import React, {useCallback, useState, useEffect} from 'react';
import { io } from 'socket.io-client';
import { Box, TextField, Button } from '@mui/material';
import settings from '../settings/settings.json';

const SearchBar = ({disabled}) => {
    const [prompt, setPrompt] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch(`${settings.api.url}/object-detection/prompt`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ prompt }),
            });
            // const data = await response.json();
            // console.log(data); // Handle the response as needed
        } catch (error) {
            console.error('Error sending prompt to backend:', error);
        }
    };

    return (
        <Box sx={{ 
            display: 'flex', 
            alignItems: 'center',
            justifyContent: 'center',
        }}>
            <form onSubmit={handleSubmit} style={{ display: 'flex' }}>
                <TextField
                    label="Enter object to detect"
                    variant="outlined"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    disabled={disabled}
                    sx={{ marginRight: 1 }} // Right margin for spacing between the TextField and Button
                />
                <Button type="submit" variant="contained" color="primary" disabled={disabled}>
                    Detect
                </Button>
            </form>
        </Box>
    );
};

export default SearchBar;
